@import "../assets/styles/custom";

$alert-height: 43px;
$alert-padding: 12px;
$alert-elements-gap: 5px;

::ng-deep .p-alert {
  display: flex;
  align-items: center;
  min-width: -moz-fit-content;
  min-width: fit-content;
  color: $primary-a-900;
  height: $alert-height;
  padding: 0;
  background: transparent;
  border-radius: 0;
  box-shadow: none;
  &-primary {
    color: $gray-scale-1;
    background-color: $primary-a-900;
  }
  &-secondary {
    background-color: $secondary-a-100;
  }
  &-warning {
    background-color: $supportive-alert-50;
  }
  &-orange {
    background-color: $supportive-orange-50;
  }
  &-success {
    background-color: $supportive-success-50;
  }

  &-container {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    padding-right: $alert-padding;
    padding-left: $alert-padding;
    white-space: nowrap;
  }

  &-icon,
  &-message-main,
  &-message-sub,
  &-action {
    & + & {
      margin-left: $alert-elements-gap;
    }
  }

  &-message-main,
  &-message-sub {
    font-family: $font-family-basic;
    font-size: $small-font-size;
  }

  &-action {
    @include resetLink;
    font-family: $font-family-basic;
    font-size: $small-font-size;
    color: $secondary-a-700;
    &:hover {
      text-decoration: underline;
    }
    ::ng-deep .p-alert-primary & {
      color: $secondary-a-500;
    }
  }

  &-close {
    @include resetButton;
    display: inline-block;
    font-size: 0;
    width: 20px;
    height: 20px;
    margin-right: -5px;
    margin-bottom: 10px;
    margin-left: 6px;
  }
}

