import {
  Component,
  Input,
  OnDestroy,
  OnInit,
  ViewChild
} from '@angular/core';
import {FormControl} from '@angular/forms';
import {SubscriptionLike} from 'rxjs';
import {MatMenuTrigger} from '@angular/material/menu';
import {IMenuButton} from '../menu-button.interface';
import {ICheckbox} from '../../checkbox/checkbox.interface';
import {IInputNumeric} from '../../input/numeric/input-numeric.interface';
import {InputNumericSizeEnum} from '../../input/numeric/input-numeric-size.enum';

@Component({
  selector: 'p-menu-tabs',
  templateUrl: './menu-tabs.component.html',
  styleUrls: ['./menu-tabs.component.scss'],
})
export class MenuTabsComponent implements OnInit, OnDestroy {
  @Input() menuTriggerName: string;
  @Input() menuList: any[];
  @Input() filterPlaceholder: string; // if there is a search and buttons
  @Input() filterAriaLabel: string;
  @Input() headerBtnFirst: IMenuButton;
  @Input() headerBtnLast: ICheckbox;
  @Input() footerBtnFirst: IMenuButton;
  @Input() footerBtnLast: IMenuButton;
  @Input() checkboxList: {
    checkbox: ICheckbox,
    inputNum: IInputNumeric
  }[];
  @ViewChild('menuTrigger') menuTrigger: MatMenuTrigger;
  private selectedList: any[] = [];
  public selectedShown: any[] = [];
  public filteredList: any[];
  public customControl: FormControl;
  private sub: SubscriptionLike;
  public tabSpecificName: string;
  public tabRelativeName: string;
  public inputNumericSize = InputNumericSizeEnum;

  ngOnInit(): void {
    this.filteredList = this.menuList;
    if (this.filterPlaceholder) {
      this.customControl = new FormControl('');
      this.sub = this.customControl.valueChanges.subscribe((value: string) => {
        this.alterList(value);
      });
    }
    if (!this.filterAriaLabel && this.filterPlaceholder) {
      this.filterAriaLabel = this.filterPlaceholder;
    }
    this.tabSpecificName = 'Specific';
    this.tabRelativeName = 'Relative';
  }

  ngOnDestroy(): void {
    if (this.filterPlaceholder && this.sub) {
      this.sub.unsubscribe();
      this.sub = null;
    }
  }

  alterList(value: string): void {
    this.filteredList = this.menuList.filter( item => item.name.toLowerCase().indexOf(value.toLowerCase()) > -1);
  }

  onOptSelected(item): void {
    console.log(item);
    this.selectedList.push(item);
  }

  selectCompleted(): void {
    this.selectedShown = this.selectedList;
    this.menuTrigger.closeMenu();
  }

  selectCanceled(): void {
    this.menuTrigger.closeMenu();
  }
}

styleUrlsData.data:
@import "../../assets/styles/custom";

$menu-padding: 8px;
$menu-item-height: 32px;
$menu-background: $gray-scale-1;
$menu-item-margin-top: 4px;
$input-text-height: 32px;
$input-text-padding: 8px;
$tabs-width: 84px;
$tabs-height: 28px;
$tabs-header-height: 60px;
$menu-header-height: $input-text-height + $menu-padding * 3 + 1px;
$menu-header-extended-height: $input-text-height + ($menu-padding * 4.5) + ($menu-item-height * 2) + 1px;
$menu-footer-height: $btn-small-height + $menu-padding * 4;
$menu-body-height: 103px;
$menu-min-width: 230px;
$menu-max-height: 75vh;
$menu-tabs-min-width: 250px;

%btn-line {
  width: 100%;
  height: 100%;
  padding-right: $menu-padding;
  padding-left: $menu-padding;
}

%menu-item {
  font-family: $font-family-basic;
  font-size: $small-font-size;
  color: $primary-a-900;
  height: $menu-item-height;
  line-height: $menu-item-height;
  padding: 0 8px;
  & + & {
    margin-top: $menu-item-margin-top;
  }
  &:hover,
  &.cdk-focused {
    background-color: $primary-c-50;
  }
  &[disabled] {
    color: $gray-scale-600;
  }
}

::ng-deep .p-menu.mat-menu-panel {
  min-width: $menu-min-width;
  max-height: $menu-max-height;
  background-color: $menu-background;
  border-radius: 0;
  box-shadow: 0 6px 10px rgba(0, 0, 0, 0.1),
  0 2px 6px rgba(0, 0, 0, 0.04),
  0 -3px 6px rgba(0, 0, 0, 0.04);
  overflow: hidden;
  &.tabs {
    min-width: $menu-tabs-min-width;
  }
  .mat-menu-content {
    height: 100%;
    padding: 0;
    position: relative;
  }
}

// container of the multiple selection menu
.p-menu-container {
  height: 100%;
}

::ng-deep .p-menu-multiple-item.mat-list-option.mat-list-item {
  @extend %menu-item;
  .mat-list-item-content.mat-list-item-content-reverse {
    flex-direction: row;
    padding: 0;
    .mat-list-text {
      display: inline;
      padding-left: 8px;
      padding-right: 0;
      @include ellipsis;
    }
  }

  .mat-pseudo-checkbox:not(.mat-pseudo-checkbox-checked) {
    border: 1px solid $gray-scale-400;
    background-color: $gray-scale-1;
  }

  // checked
  .mat-pseudo-checkbox-checked {
    background-color: $secondary-a-700;
    &:after {
      width: 7px;
      height: 2px;
      top: 3.4px;
      left: 1.5px;
    }
  }

  // disabled
  &.mat-list-item-disabled {
    background-color: transparent;
    .mat-pseudo-checkbox:not(.mat-pseudo-checkbox-checked) {
      background-color: $primary-c-50;
      border-color: $gray-scale-400;
    }
    .mat-pseudo-checkbox-checked {
      background-color: $secondary-a-100;
    }
    .mat-list-text {
      color: $gray-scale-600;
    }
  }
}

// results on the trigger
.p-menu-trigger-result {
  & + & {
    margin-left: 5px;
  }
  &:not(:last-child):after {
    content: ',';
    display: inline;
  }
}

// search field
::ng-deep .p-menu {
  &-form-field {
    width: 100%;
    height: $input-text-height;
    ::placeholder {
      font-style: italic;
      color: $gray-scale-700;
    }

    .mat-input-element {
      font-family: $font-family-basic;
      font-size: $small-font-size;
      color: $primary-a-900;
      max-width: calc(100% - #{$input-text-padding} * 2 - 2px);
      height: $input-text-height - 2px;
      margin-top: 1px;
      padding-left: $input-text-padding;
      padding-right: $input-text-padding;
      caret-color: $primary-a-900;
    }

    &.mat-form-field-appearance-outline {
      &:hover {
        .mat-form-field-outline,
        .mat-form-field-outline-thick {
          color: $primary-a-900;
        }
      }
      .mat-form-field-wrapper {
        height: 100%;
        margin-top: 0;
        margin-bottom: 0;
        padding-bottom: 0;
      }
      .mat-form-field-flex {
        height: 100%;
        padding: 0;
        margin-top: 0;
      }
      .mat-form-field-outline {
        top: 0;
      }
      .mat-form-field-infix {
        display: flex;
        justify-content: center;
        align-items: center;
        height: $input-text-height;
        padding: 0;
        border-top: none;
      }
      .mat-form-field-outline-start {
        border-radius: 2px 0 0 2px;
      }
      .mat-form-field-outline-end {
        border-radius: 0 2px 2px 0;
      }
      .mat-form-field-outline,
      .mat-form-field-outline-thick {
        color: $gray-scale-400;
        .mat-form-field-outline-start,
        .mat-form-field-outline-end,
        .mat-form-field-outline-gap {
          border-width: 1px;
        }
      }
      &.mat-focused {
        .mat-form-field-outline,
        .mat-form-field-outline-thick {
          color: $primary-a-900;
        }
      }
    }
  }
}

.p-menu {
  &-header {
    width: 100%;
    padding: $menu-padding;
    background-color: $menu-background;
    border-bottom: 1px solid $gray-scale-400;
    position: absolute;
    z-index: 1;

    .p-menu-form-field + .p-menu-btn-line {
      margin-top: $menu-padding;
    }
    .p-menu-btn-line + .p-menu-btn-line {
      margin-top: 4px;
    }
  }

  &-body {
    height: calc(100% - #{$menu-footer-height});
    padding-top: $menu-padding;
    padding-right: 0;
    padding-bottom: 0;
    padding-left: $menu-padding;
    // there are buttons after input
    .p-menu-header.extended + & {
      min-height: $menu-header-extended-height + $menu-body-height + $menu-footer-height;
      padding-top: $menu-header-extended-height;
    }

    &-container {
      height: 100%;
      padding-top: 0;
      overflow: auto;
    }
  }

  &-footer {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: $menu-footer-height;
    padding-top: $menu-padding * 2;
    padding-bottom: $menu-padding * 2;
    background-color: $menu-background;
    position: absolute;
    bottom: 0;
    left: 0;
    z-index: 1;
    &-btn {
      & + & {
        margin-left: $menu-padding;
      }
    }
  }

  &-btn-line {
    display: block;
    width: 100%;
    height: $menu-item-height;
    ::ng-deep .p-btn {
      @extend %btn-line;
      justify-content: flex-start;
    }
    ::ng-deep .p-checkbox {
      display: inline-flex;
      align-items: center;
      @extend %btn-line;
      &:not(.mat-checkbox-disabled):hover {
        background-color: $primary-c-50;
      }
    }
  }
}

// tabs
::ng-deep .p-menu-tabs {
  max-height: $menu-max-height;
  height: 100%;
  .mat-tab-list {
    width: 100%;
    height: $tabs-header-height;
  }
  .mat-tab-header {
    border-color: $gray-scale-400;
  }
  .mat-tab-labels {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100%;
  }
  .mat-tab-label {
    font-family: $font-family-basic;
    font-size: $small-font-size;
    font-weight: 500;
    width: $tabs-width;
    height: $tabs-height;
    min-width: unset;
    padding: 0;
    opacity: 1;
    transition: all 400ms;
    &-active {
      color: $gray-scale-1;
      background-color: $secondary-a-700;
      font-weight: 600;
      &:hover ,
      &:focus {
        background-color: $secondary-a-800;
      }
      &:disabled {
        color: $gray-scale-600;
        background-color: $secondary-a-50;
      }
    }
    &:not(.mat-tab-label-active) {
      color: $secondary-a-700;
      background-color: $gray-scale-1;
      border: 1px solid $secondary-a-700;
      font-weight: 400;
      &:hover,
      &:focus {
        background-color: $primary-c-50;
      }
      &:disabled {
        color: $gray-scale-600;
        border-color: $secondary-a-500;
      }
    }
  }
  .mat-ink-bar {
    display: none;
  }

  &-panel {
    height: 100%;
    &.relative {
      height: calc(100% - #{$tabs-header-height});
      padding-top: $menu-padding;
      padding-left: $menu-padding;
    }
  }
}

// relative panel styles
.p-menu-check {
  &-list {
    @include resetUl;
    height: 100%;
    padding-right: $menu-padding;
    overflow: auto;
  }
  &-item {
    display: flex;
    align-items: center;
    overflow: hidden;
    @extend %menu-item;
    ::ng-deep .p-checkbox {
      display: inline-flex;
    }
  }
  &-input {
    margin-left: 20px;
    margin-top: 13px;
  }
}

