@import "../assets/styles/custom";

::ng-deep .p-btn {
  @include resetButton;
  font-family: $font-family-basic;
  font-size: $small-font-size;
  font-weight: 500;
  line-height: 20px;
  min-width: 80px;
  padding-right: $gap-double-plus;
  padding-left: $gap-double-plus;
  border-radius: $btn-border-radius;
  white-space: nowrap;
  transition: all 400ms;

  &-large {
    height: $btn-large-height;
  }

  &-small {
    height: $btn-small-height;
  }

  &:disabled {
    pointer-events: none;
  }

  &-primary {
    color: $gray-scale-1;
    background-color: $secondary-a-700;
    &:hover {
      background-color: $secondary-a-800;
    }
    &:focus {
      background-color: $secondary-a-800;
    }
    &:disabled {
      color: $gray-scale-700;
      background-color: $secondary-a-50;
    }
  }

  &-secondary {
    color: $secondary-a-700;
    background-color: $gray-scale-1;
    border: 1px solid $secondary-a-700;
    &:hover {
      background-color: $primary-c-50;
    }
    &:focus {
      background-color: $primary-c-50;
    }
    &:disabled {
      color: $gray-scale-700;
      border-color: $secondary-a-500;
    }
  }

  &-tertiary {
    color: $secondary-a-700;
    background-color: $gray-scale-1;
    &:hover {
      background-color: $primary-c-50;
    }
    &:focus {
      background-color: $primary-c-50;
    }
    &:disabled {
      color: $gray-scale-700;
    }
  }

  &-distructive {
    color: $gray-scale-1;
    background-color: $supportive-alert-500;
    &:hover {
      background-color: $supportive-alert-700;
    }
    &:focus {
      background-color: $supportive-alert-700;
    }
    &:disabled {
      color: $gray-scale-700;
      background-color: $supportive-alert-50;
    }
  }

  // class of the action-button
  &-icon-before {
    padding-left: $gap-double;
    .p-btn-label {
      margin-left: $gap-base;
    }
  }
  // class of the action-button
  &-icon-after {
    padding-right: $gap-double;
    .p-btn-label {
      margin-right: $gap-base;
    }
  }

  // class of the icon
  &-icon-pre,
  &-icon-post {
    display: inline-flex;
    justify-content: center;
    align-items: center;
  }

  &-isIcon {
    line-height: unset;
    min-width: unset;
    padding: $gap-small;
    &:disabled {
      color: $gray-scale-700;
    }
  }
}

