    width: calc(100% - #{$gap-base} - #{$gap-small});
    @include ellipsis;
  }

  ::ng-deep .mat-form-field-infix {
    width: auto;
  }

  &-prefix {
    margin-left: $gap-base;
  }

  .p-input-text-input {
    cursor: pointer;
  }

  ::ng-deep .mat-form-field-prefix + .mat-form-field-infix .p-input-text-input {
    padding-left: $gap-small;
  }

  ::ng-deep .mat-form-field-suffix {
    display: flex;
    align-items: center;
  }

  // hover
  &:not(.mat-form-field-disabled):not(.mat-form-field-invalid):hover {
    color: $secondary-a-800;

    ::placeholder {
      color: $secondary-a-800;
    }

    .p-input-text-input {
      color: $secondary-a-800;
      @include ellipsis;
    }
  }
}

