@import "../../assets/styles/custom";

$table-header-max-width: 240px;
$row-height: 52px;

.input-table {
  border-collapse: collapse;

  &-static-title {
    margin-bottom: 15px;
  }

  &-row {
    height: $row-height;
    opacity: 1;
    transition: opacity 300ms;
    &.hidden {
      opacity: 0;
    }
  }

  &-header {
    font-family: $font-family-basic;
    font-size: $base-font-size;
    font-weight: 400;
    color: $gray-scale-700;
    text-align: left;
    text-transform: capitalize;
    max-width: $table-header-max-width;
    padding-right: $gap-double - $gap-small;
    padding-bottom: $gap-base;
    &-container {
      display: flex;
      align-items: center;
    }
    &-text {
      @include clamp;
      margin-right: $gap-small;
    }
    &-attention {
      display: inline-block;
      align-self: flex-end;
      margin-right: $gap-small;
    }
    &-required {
      font-size: $big-font-size;
      color: $supportive-alert-500;
      align-self: flex-end;
      margin-right: $gap-small;
    }
  }

  &-data {
    ::ng-deep .mat-form-field-infix {
      padding-top: 0;
      border-top-width: 5px;
    }
  }

  &-trigger {
    @include resetButton;
    color: $primary-a-900;
    margin-bottom: 15px;
    &-icon {
      display: flex;
      margin-right: 12.5px;
      transform: rotate(0);
      transition: transform 280ms ease-in-out;
      &.closed {
        transform: rotate(-90deg);
      }
    }
  }

  &-container {
    height: calc( var(--row-amount) * #{$row-height});
    overflow: hidden;
    transition: height 280ms ease-in-out;
    &.collapsed {
      height: 0;
    }
  }
}

