@import "../sidenav.component";

$header-height: 62px;
$padding-left-main: 36px;
$padding-left-content: 56px;
$expand-width: 14px;

.p-sidenav {
  ::ng-deep &.main .mat-expansion-panel:not([class*=mat-elevation-z]) {
    border-top: $border;
  }
  ::ng-deep &.main .mat-expansion-panel-header {
    font-family: $font-family-accent;
    font-weight: 600;
    font-size: $small-font-size;
    letter-spacing: 0.08em;
    text-transform: uppercase;
    color: $primary-a-900;
    padding-right: $padding-right;
    padding-left: $padding-left-main;
    border-right: $border;
    border-left: $border;
    &,
    &.mat-expanded {
      height: $header-height;
    }
    &.mat-expanded {
      color: $secondary-a-700;
      font-weight: 700;
    }
  }

  .p-sidenav-header-icon {
    margin-right: 10px;
  }

  &-content-item {
    border-top: 1px solid $gray-scale-400;
  }

  &-content-link {
    @include resetLink;
    display: flex;
    align-items: center;
    font-family: $font-family-basic;
    font-weight: 600;
    font-size: $small-font-size;
    text-transform: capitalize;
    width: 100%;
    height: 100%;
    padding-right: $padding-right;
    padding-left: $padding-left-content;
    border-right: $border;
    border-left: $border;
    outline: none;
    position: relative;
    &:focus,
    &:hover {
      background: $primary-c-50;
    }
    &.selected {
      @extend %selected;
    }
  }

  ::ng-deep .mat-expansion-indicator:after {
    content: 'Expand';
    display: block;
    font-size: 0;
    width: $expand-width;
    height: $expand-width;
    background-image: url('../../assets/images/plus.svg');
    background-repeat: no-repeat;
    background-position: center center;
    background-size: initial;
    border: none;
    padding: 0;
    transform: none;
  }

  ::ng-deep .mat-expanded .mat-expansion-indicator::after {
    content: 'Collapse';
    background-image: url('../../assets/images/minus.svg');
  }
}

